mapper.select:
Select T1.buyResult, T1.groupID, T4.groupName, T2.companyCode, T3.companyName, COUNT(T1.productCode) as orderCnt, SUM(T2.productPrice) as orderAmt From ( Select TEMP.buyResult , TEMP.stockCnt, TEMP.safeStock, TEMP.holdStock, TEMP.groupID, TEMP.productCode From ( Select ifnull(I3.buyResult,'01') as buyResult , ifnull(I2.stockDate,'19990101') as stockChk,/*재고기준일*/ DATE_FORMAT(ifnull(I3.orderDateTime,'1999-01-01 00:00:00'), '%Y-%m-%d') as orderChk,/*발주기준일*/ DATE_FORMAT(ifnull(I3.buyDateTime,'1999-01-01 00:00:00'), '%Y-%m-%d') as buyChk,/*발주완료기준일*/ ifnull(I2.stockCnt,0) as stockCnt, ifnull(I1.safeStock,0) as safeStock, ifnull(I1.holdStock,0) as holdStock, I1.groupID, I1.productCode From adStockMaster I1 Left Join( Select Max(stockDate) as stockDate,stockCnt,groupID,productCode From adStockDetail Group by stockCnt,groupID,productCode ) As I2 On I1.productCode = I2.productCode And I1.groupID = I2.groupID Left Join( Select E1.orderCode , E1.productCode, E2.groupID , E2.buyResult , E2.buyDateTime, E2.orderDateTime From adOrderDetail E1 Left Join adOrder E2 On E1.orderCode=E2.orderCode ) As I3 On I1.productCode = I3.productCode And I1.groupID = I3.groupID Where 1=1
AND I1.groupId = #{con_groupId}
) As TEMP Left Join adProductMaster T1 On TEMP.ProductCode = T1.ProductCode Where TEMP.stockCnt < TEMP.safeStock And TEMP.stockChk > TEMP.buyChk
AND TEMP.buyResult = #{con_orderState}
) T1 Left Join adProductMaster T2 On T1.ProductCode = T2.ProductCode Left Join adCompany T3 On T2.companyCode = T3.companyCode Left Join ofGroup T4 On T1.groupID = T4.groupID Group By T1.groupID, T2.companyCode LIMIT ${page_limit_val1} , ${page_limit_val2}

SELECT count(*) AS TOT_COUNT From ( Select T1.groupID, T2.companyCode From ( Select TEMP.buyResult , TEMP.stockCnt, TEMP.safeStock, TEMP.holdStock, TEMP.groupID, TEMP.productCode From ( Select ifnull(I3.buyResult,'01') as buyResult , ifnull(I2.stockDate,'19990101') as stockChk,/*재고기준일*/ DATE_FORMAT(ifnull(I3.orderDateTime,'1999-01-01 00:00:00'), '%Y-%m-%d') as orderChk,/*발주기준일*/ DATE_FORMAT(ifnull(I3.buyDateTime,'1999-01-01 00:00:00'), '%Y-%m-%d') as buyChk,/*발주완료기준일*/ ifnull(I2.stockCnt,0) as stockCnt, ifnull(I1.safeStock,0) as safeStock, ifnull(I1.holdStock,0) as holdStock, I1.groupID, I1.productCode From adStockMaster I1 Left Join( Select Max(stockDate) as stockDate,stockCnt,groupID,productCode From adStockDetail Group by stockCnt,groupID,productCode ) As I2 On I1.productCode = I2.productCode And I1.groupID = I2.groupID Left Join( Select E1.orderCode , E1.productCode, E2.groupID , E2.buyResult , E2.buyDateTime, E2.orderDateTime From adOrderDetail E1 Left Join adOrder E2 On E1.orderCode=E2.orderCode ) As I3 On I1.productCode = I3.productCode And I1.groupID = I3.groupID Where 1=1
AND I1.groupId = #{con_groupId}
) As TEMP Left Join adProductMaster T1 On TEMP.ProductCode = T1.ProductCode Where TEMP.stockCnt < TEMP.safeStock And TEMP.stockChk > TEMP.buyChk
AND TEMP.buyResult = #{con_orderState}
) T1 Left Join adProductMaster T2 On T1.ProductCode = T2.ProductCode Left Join adCompany T3 On T2.companyCode = T3.companyCode Left Join ofGroup T4 On T1.groupID = T4.groupID Group By T1.groupID, T2.companyCode ) As TOT
