mapper.select:
Select T4.codeName as recoveryStateView , T1.recoveryState, T1.recoveryCode, T2.recoveryClosingDate, DATE_FORMAT(T2.collectDateTime, '%Y-%m-%d') as collectDateTime, T1.groupId, T3.groupName, 0 as recoveryResultCnt, 0 as recoveryResultPrice From adRecovery T1 Left Join adCollect T2 On T1.collectCode=T2.collectCode Left Join ofGroup T3 On T1.groupId = T3.groupId Left Join ofCode T4 On T1.recoveryState = T4.codeId and T4.codegroupId = 'RE02' Where T2.deletedYn='N'
AND T1.groupId = #{con_groupId}
AND T1.recoveryState = #{con_recoveryState}
Group by T4.codeName, T1.recoveryState, T1.recoveryCode, T2.recoveryClosingDate, T2.collectDateTime, T1.groupId, T3.groupName LIMIT ${page_limit_val1} , ${page_limit_val2}

SELECT count(*) AS TOT_COUNT From ( Select T4.codeName as recoveryStateView , T1.recoveryState, T1.recoveryCode, T2.recoveryClosingDate, DATE_FORMAT(T2.collectDateTime, '%Y-%m-%d') as collectDateTime, T1.groupId, T3.groupName, 0 as recoveryResultCnt, 0 as recoveryResultPrice From adRecovery T1 Left Join adCollect T2 On T1.collectCode=T2.collectCode Left Join ofGroup T3 On T1.groupId = T3.groupId Left Join ofCode T4 On T1.recoveryState = T4.codeId and T4.codegroupId = 'RE02' Where T2.deletedYn='N'
AND T1.groupId = #{con_groupId}
AND T1.recoveryState = #{con_recoveryState}
Group by T4.codeName, T1.recoveryState, T1.recoveryCode, T2.recoveryClosingDate, T2.collectDateTime, T1.groupId, T3.groupName ) As TOT

mapper.update:
		update adRecovery
			set
				recoveryClosingDate = #{recoveryClosingDate}
               ,recoveryUserId = #{regUserId}
               ,recoveryDateTime = now()
               ,recoveryState = '02'
		where recoveryCode = #{recoveryCode}

		update adRecovery 
			set recoveryUserId  = #{recoveryUserId}
        		,recoveryDateTime = now()
        		,recoveryState = #{recoveryState}
		where recoveryCode = #{recoveryCode}

	
		update adRecoveryDetail 
			set stockDate = #{stockDate}
        		,stockCnt = #{stockCnt}
        		,recoveryCnt = #{recoveryCnt}
        		,addCnt = #{addCnt}
				,lossCnt = #{lossCnt}
         		,etc = #{etc}
			    ,updateUserId = #{updateUserId}
            	,updateDateTime = now()
		where recoveryCode = #{recoveryCode}
		  And productCode =  #{productCode}

		update adRecovery 
			set completeUserId  = #{completeUserId}
        		,completeDateTime = now()
        		,recoveryState = #{recoveryState}
		where recoveryCode = #{recoveryCode}

	
		update adRecoveryDetail 
			set recoveryResultCnt = #{recoveryResultCnt}
			    ,recoveryYn='Y'
			    ,updateUserId = #{updateUserId}
            	,updateDateTime = now()
		where recoveryCode = #{recoveryCode}
		  And productCode =  #{productCode}
