mapper.select:
Select T6.codeName as recoveryStateView , T4.recoveryState, T4.recoveryCode, T4.recoveryClosingDate, T4.regDateTime, T4.groupId, T5.groupName, Sum(T4.stockCnt) as recoveryResultCnt, Sum(T4.recoveryResultPrice) as recoveryResultPrice From ( Select T1.recoveryState, T1.recoveryCode, T1.recoveryClosingDate, T1.regDateTime, T1.groupId, T2.recoveryResultCnt, K1.stockCnt, T2.productCode, T3.productPrice, (T3.productPrice * K1.stockCnt) As recoveryResultPrice, T3.deletedYn From adRecovery T1 Left Join adRecoveryDetail T2 On T1.recoveryCode = T2.recoveryCode Left Join adProductMaster T3 On T2.productCode = T3.productCode Left Join ( Select groupId,productCode,stockDate, (Select stockCnt From adStockDetail Where A1.groupId=groupId and A1.productCode=productCode and A1.stockDate=stockDate) as stockCnt From ( Select groupId,productCode , max(stockDate) as stockDate From adStockDetail Group By groupId,productCode ) A1 ) As K1 On T1.groupId = K1.groupId And T2.productCode = K1.productCode Where T1.regDateTime between concat(#{start_recoveryDate},' 00:00:00') and concat(#{end_recoveryDate},' 23:59:59') ) As T4 Left Join ofGroup T5 On T4.groupId = T5.groupId Left Join ofCode T6 On T4.recoveryState = T6.codeId and T6.codegroupId = 'RE01' Where T4.deletedYn='N' And ifnull(T4.stockCnt,0) > 0
AND T4.groupId = #{con_groupId}
AND T4.recoveryState = #{con_recoveryState}
Group by T6.codeName, T4.recoveryState, T4.recoveryCode, T4.recoveryClosingDate, T4.regDateTime, T4.groupId, T5.groupName LIMIT ${page_limit_val1} , ${page_limit_val2}

SELECT count(*) AS TOT_COUNT From ( Select T6.codeName as recoveryStateView , T4.recoveryState, T4.recoveryCode, T4.recoveryClosingDate, T4.regDateTime, T4.groupId, T5.groupName, Sum(T4.stockCnt) as recoveryResultCnt, Sum(T4.recoveryResultPrice) as recoveryResultPrice From ( Select T1.recoveryState, T1.recoveryCode, T1.recoveryClosingDate, T1.regDateTime, T1.groupId, T2.recoveryResultCnt, K1.stockCnt, T2.productCode, T3.productPrice, (T3.productPrice * K1.stockCnt) As recoveryResultPrice, T3.deletedYn From adRecovery T1 Left Join adRecoveryDetail T2 On T1.recoveryCode = T2.recoveryCode Left Join adProductMaster T3 On T2.productCode = T3.productCode Left Join ( Select groupId,productCode,stockDate, (Select stockCnt From adStockDetail Where A1.groupId=groupId and A1.productCode=productCode and A1.stockDate=stockDate) as stockCnt From ( Select groupId,productCode , max(stockDate) as stockDate From adStockDetail Group By groupId,productCode ) A1 ) As K1 On T1.groupId = K1.groupId And T2.productCode = K1.productCode Where T1.regDateTime between concat(#{start_recoveryDate},' 00:00:00') and concat(#{end_recoveryDate},' 23:59:59') ) As T4 Left Join ofGroup T5 On T4.groupId = T5.groupId Left Join ofCode T6 On T4.recoveryState = T6.codeId and T6.codegroupId = 'RE01' Where T4.deletedYn='N' And ifnull(T4.stockCnt,0) > 0
AND T4.groupId = #{con_groupId}
AND T4.recoveryState = #{con_recoveryState}
Group by T6.codeName, T4.recoveryState, T4.recoveryCode, T4.recoveryClosingDate, T4.regDateTime, T4.groupId, T5.groupName ) As TOT

mapper.update:
		update adRecovery
			set
				recoveryClosingDate = #{recoveryClosingDate}
               ,recoveryUserId = #{regUserId}
               ,recoveryDateTime = now()
               ,recoveryState = '02'
		where recoveryCode = #{recoveryCode}
